import React, { Component } from "react";
import "../css/main.css";
import $ from "jquery";
// eslint-disable-next-line
class CadUser extends Component {
  constructor() {
    super();
    this.state = { nome: "", email: "" };
  }

  cadastrarUser(e) {
    console.log("enviado");
    console.log(this);
    $.ajax({
      url: "http://cdc-react.herokuapp.com/api/autores/",
      dataType: "json",
      contentType: "application/json",
      type: "post",
      data: JSON.stringify({
        nome: this.state.nome,
        email: this.state.email,
        senha: "123"
      }),
      success: (resposta: any) => {
        console.log("sucesso");
      },
      error: () => {
        console.log("erro");
      }
    });
  }

  render() {
    return (
      <div className="wrap-contact3">
        <label>
          Nome:
          <input type="text" name="inptNome" />
        </label>
        <br />
        <label>
          Email:
          <input type="text" name="inptEmail" />
        </label>
        <br />
        <input
          className="contact3-form-btn"
          type="submit"
          value="Submit"
          onClick={() => this.cadastrarUser(2).bind()}
        />
      </div>
    );
  }
}

export default CadUser;
